		public static async Task RefreshNotificationData()
		{
			try
			{
				await SetAllNotificationData();
			}
			catch (Exception ex)
			{
				throw ex;
			}

		}

		public static ObservableCollection<NotificationItem> GetNotificationData(int PageNum)
		{
			switch (PageNum)
			{
				case 0:
					return _allNotifList;

				case 1:
					return new ObservableCollection<NotificationItem>(_allNotifList.Where(w => w.IsReaded == false).ToList());
					//return _allNotifList.Where(w => w.IsReaded == false).ToList().ToObservable() as ObservableCollection<NotificationItem>;

				case 2:
					return new ObservableCollection<NotificationItem>(_allNotifList.Where(w => w.IsReaded == true).ToList());
					//return _allNotifList.Where(w => w.IsReaded == true).ToObservable() as ObservableCollection<NotificationItem>;

				case 3:
					return new ObservableCollection<NotificationItem>(_allNotifList.Where(w => w.IsFavorite == true).ToList());
					//return _allNotifList.Where(w => w.IsFavorite == true).ToObservable() as ObservableCollection<NotificationItem>;

				default:
					return null;
			}
		}

