		private static ImageSource GetImage(string str)
		{
			byte[] bytes = Convert.FromBase64String(str);

			var img = ImageSource.FromStream(() => new MemoryStream(bytes));

			return img;
		}

		public static void DeleteItem(NotificationItem item) 
		{
			_allNotifList.Remove(item);
		}

		public static void UpdateItem(NotificationItem item)
		{
			bool newIsFavorite = item.IsFavorite ? false : true;

			_allNotifList.Where(w => w.Title == item.Title).Select(s => { s.IsFavorite = newIsFavorite; return s; }).ToList();
		}

